import base64


def base64_to_html(bs64_str, save_path, file_name):
    """
    将base64编码字符串保存进文件中
    :param base64_str:
    :param save_path:
    :param file_name:
    :return:
    """
    imgdata = base64.b64decode(bs64_str)
    # 将二进制数据装换为图片
    with open(save_path + '/' + file_name, "wb") as f2:
        f2.write(imgdata)


if __name__ == '__main__':
    str = ""
    base64_to_html(str, '.', 'test.html')
